import 'package:flutter/material.dart';
import 'package:readmore/readmore.dart';

class CustomScrollviewScreen extends StatefulWidget {
  const CustomScrollviewScreen({super.key});

  @override
  State<CustomScrollviewScreen> createState() => _CustomScrollviewScreenState();
}

class _CustomScrollviewScreenState extends State<CustomScrollviewScreen> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: CustomScrollView(
        slivers: [_sliverAppBar(), _sliverToBoxAdapter()],
      ),
    );
  }

  _sliverAppBar() {
    return SliverAppBar(
      title: Row(
        children: [
          _iconAppBar(
              icon: Icon(
                Icons.arrow_back,
                size: 20,
              ),
              onTap: () {
                // Navigator.push(context, MaterialPageRoute(builder: (context) => HomeMovieScreen()));
              })
        ],
      ),
      bottom: PreferredSize(
          preferredSize: Size.fromHeight(20),
          child: Container(
            child: Center(
                child: Text(
              "LpBank",
              style: TextStyle(fontSize: 25, fontWeight: FontWeight.bold),
            )),
            width: double.maxFinite,
            decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.only(
                    topLeft: Radius.circular(20),
                    topRight: Radius.circular(20))),
          )),
      expandedHeight: 200,
      pinned: true,
      backgroundColor: Colors.white,
      flexibleSpace: FlexibleSpaceBar(
        background: Image.asset(
          "assets/images/logo.png",
          width: double.maxFinite,
          fit: BoxFit.cover,
        ),
      ),
    );
  }

  _sliverToBoxAdapter() {
    return SliverToBoxAdapter(
      child: Column(
        children: [
          Container(
              margin: EdgeInsets.only(
                left: 10,
              ),
              padding: EdgeInsets.all(10),
              child: ReadMoreText(
                  trimLines: 15,
                  colorClickableText: Colors.blue,
                  trimMode: TrimMode.Line,
                  trimCollapsedText: 'Xem thêm',
                  trimExpandedText: ' Thu gọn',
                  style: TextStyle(height: 2),
                  "")),
        ],
      ),
    );
  }

  _iconAppBar({required VoidCallback onTap, required Icon icon}) {
    return InkWell(
      onTap: onTap,
      child: Center(
        child: ClipOval(
          child: Container(
              height: 30, width: 30, color: Colors.white, child: icon),
        ),
      ),
    );
  }
}
